/**
JavaScript function to centralise creation of a database connection. The
connection won't be open, the caller still needs to do this but all constants
will be instantiated here

@return dbConn returns an instance of a returns a new DatabaseConnection object
*/
function Get_CDS_Connection() {
var dbConn = $gc('dbConn');

if (!dbConn || dbConn.getConnection().isClosed()) {
var dbConn = DatabaseConnectionFactory.createDatabaseConnection($('CDS_Driver_Name'), $('CDS_DB_URL'), $('CDS_DB_UserName'), $('CDS_DB_Password'));
$gc('dbConn', dbConn);
}

return dbConn;
}

function get_latest_episode_for_episodeID(episodeIDList) {
  var dbConn = Get_CDS_Connection();
  var sql = "SELECT * FROM cds_data WHERE EpisodeID = ? ORDER BY EpisodeDateTime DESC LIMIT 1";
  var latestEpisode = "";
  	try {
  		var latestEpisode = dbConn.executeCachedQuery(sql, episodeIDList);
  	} catch (exception) {
  		logger.error("Error when inserting data: " + exception);
  	} finally {
  		dbConn.close();
  	}
  return latestEpisode;
}

