			    	  params.add(episodeId);
			    	  params.add(testLISCode);
			    	  params.add(testLISName);
			    	  params.add(testValue);
			    	  params.add(processed_YN);

				      var dbConn = Get_CDS_Connection();
				      var sql = "INSERT INTO cds_data(PatientID, PatientFamilyName, PatientGivenName, EpisodeDateTime, EpisodeID, testLISCode, TestLISName, TestValue, Processed_YN) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)";
				      try {
				        var result = dbConn.executeUpdate(sql, params);
				      } catch (exception) {
				        logger.error("Error when inserting data: " + exception);
				      } finally {
				        dbConn.close();
				      }
				  }

function Check_Unprocessed_Data() {
	var dbConn = Get_CDS_Connection();
	var rowCount = 0;

	var sqlResult = null;
	try {
		var returnValue = 1;

		var sqlQuery = "call rd_staging.rdSelCDSCount()";

		logger.trace("SQL " + sqlQuery);

		sqlResult = dbConn.executeCachedQuery(sqlQuery);
		if (sqlResult.next()) {
			rowCount = sqlResult.getInt('StatusCode');
		}
	} finally {
		// Do nothing right now...
		if  (sqlResult != null) {
			//sqlResult.close();
		}
	}
	return rowCount;
}

function recordsToProcess() {	
	if (Check_Unprocessed_Data() > 0) {
		return true;
	} else {
		return false;
	}
}

