function Change_State(Channel, State) {
	const threadSleepTime = 10000; // ten seconds
	const millisecondsPerMinute = 60000;
	const maxAttempts = ($('CDS_Max_Wait_Minutes') * millisecondsPerMinute) / threadSleepTime;

	var attempts = 0;
	var channelState;

	channelState = ChannelUtil.getChannelState(Channel);
	if (channelState == State) {
		logger.debug('Channel already ' + State + ' no need to change state.');
		return true;
	} else {
		// Channel is running, need to check whether there are records to process...
		if (State == "Started"){
			logger.info('Starting Channel: ' + Channel);
			ChannelUtil.startChannel(Channel);
		} else {
			logger.info('Stopping Channel: ' + Channel);
			ChannelUtil.stopChannel(Channel);
		}
		channelState = ChannelUtil.getChannelState(Channel);
		while ((channelState != State) && (attempts < maxAttempts)) {
			logger.debug("Waiting for channel " + Channel);
			channelState = ChannelUtil.getChannelState(Channel);
			if (channelState != State) {
				java.lang.Thread.sleep(threadSleepTime);
			}

			attempts ++;
		}
		logger.debug('Channel ' + Channel + ' is ' + channelState);
		if (channelState == State) {
			logger.debug('Successfully changed state of ' + Channel);
			return true;
		} else {
			return false;
		}
	}
}

function Update_CDS_Status(ResponseStatus, currBatchSeq, currEpisodeID) {
	logger.trace("Resp Status: " + ResponseStatus);

	var cdsStatus;

    if (ResponseStatus != "SENT") {
		cdsStatus = "E";
		logger.trace("Response code from web service call was not SENT, response:" + ResponseStatus);
		// Call the stored proc setting the status to "E" for the error cases
		var dbConn = Get_CDS_Connection();
		try {
			var returnValue = 1;
			var sqlQuery = "call rd_staging.rdUpdCDSStatus(" + currBatchSeq + ",'" + currEpisodeID + "','" + cdsStatus + "')";

			logger.trace("SQL " + sqlQuery);

			var sqlResult = dbConn.executeCachedQuery(sqlQuery);
			if (sqlResult.next()) {
				logger.debug("PKS - Update_CDS_Status - Info: " + sqlResult.getString("Message"));
			}
		}
		finally {
		//	dbConn.close();
		}
	}
}

function Escape_String(str) {
		// Replace the following...
		// ' - Single quote
		// " - Double quote
		// % - Percent
		// _ - Underscore
		// ; - Semi colon
		// No need to replace carriage returns, linefeeds and tabs as the incoming file is tab separated and row terminators
		// are carriage returns and linefeeds
		return str ? String(str).replace(/('|"|\%|\_|\;)/g, '\\$1') : str;
		}

function transformTestValue(result) {
	if (result != null && result != '') {
		if (result.indexOf(",") >= 0) {
			result = result.replace(/,/g, ".");
		}
	}
	return result;
}

function transformRange(range) {
	if (range != null && range != '') {
		if (range.indexOf(",") >= 0) {
			range = range.replace(/,/g, ".");
		}
		if (!isValidRange(range)) {
			logger.error("Invalid range dropped:  " + range + ".  Data was stored without the range.");
			range = "";
		}
	}
	return range;
}

function isValidRange(range) {
	var regex = /([\s|-]{0,1}\d{1,4}(\.\d{1,8}){0,1}\s{0,1}-\s{0,1}\d{1,4}(\.\d{1,8}){0,1}\s{0,1})|(\s{0,1}[<>]={0,1}\s{0,3}-{0,1}\d{1,4}(\.\d{1,8}){0,1})/;
	return regex.test(range);
}

