	encryptedValue = encryptedValue.replace(/l/g, 'k');
	encryptedValue = encryptedValue.replace(/k/g, 'l');
	encryptedValue = encryptedValue.replace(/j/g, 'z');
	encryptedValue = encryptedValue.replace(/h/g, 'x');
	encryptedValue = encryptedValue.replace(/g/g, 'c');
	encryptedValue = encryptedValue.replace(/f/g, 'v');
	encryptedValue = encryptedValue.replace(/d/g, 'b');
	encryptedValue = encryptedValue.replace(/s/g, 'n');
	encryptedValue = encryptedValue.replace(/a/g, 'm');
	encryptedValue = encryptedValue.replace(/p/g, '9');
	encryptedValue = encryptedValue.replace(/o/g, '8');
	encryptedValue = encryptedValue.replace(/i/g, '7');
	encryptedValue = encryptedValue.replace(/u/g, '6');
	encryptedValue = encryptedValue.replace(/y/g, '5');
	encryptedValue = encryptedValue.replace(/t/g, '4');
	encryptedValue = encryptedValue.replace(/r/g, '3');
	encryptedValue = encryptedValue.replace(/e/g, '2');
	encryptedValue = encryptedValue.replace(/w/g, '1');
	encryptedValue = encryptedValue.replace(/q/g, '0');
	return encryptedValue;
}

function calculate_age_in_years(dateOfBirth) {
	var today = new Date();
	var birthDate = new Date(dateOfBirth);
	var age = today.getFullYear() - birthDate.getFullYear();
	var m = today.getMonth() - birthDate.getMonth();
	if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
		age--;
	}
	return age;
}

function buildStringIfNotNull(value) {
	if (value == null || value == '') {
		return null;
	}
	return "'" + value + "'";
}
